mapper.select:
SELECT a.*, u.user_id, u.nick_name, u.user_sex, u.user_role, u.user_exprience, u.user_email, u.user_birthday, u.user_head_photo, art.article_type_id, art.parent_id, art.article_type_name, art.priority, art.article_type_create_time, art.article_type_last_edit_time, c.comment_num, p.* FROM(select * from article limit #{rowIndex},#{pageSize}) as a LEFT JOIN user as u on a.user_id=u.user_id LEFT JOIN (select * from article_type
parent_id=#{article.articleType.parentArticleType.articleTypeId}
) as art on a.article_type_id= art.article_type_id LEFT JOIN (select distinct(article_id),count(article_id) as comment_num from user_comment group by article_id) as c on a.article_id=c.article_id LEFT JOIN photo as p on a.article_id=p.article_id
a.article_type_id = #{article.articleType.articleTypeId}
and year(article_create_time)=year(#{article.articleCreateTime}) and month(article_create_time)= month(#{article.articleCreateTime})
and a.article_create_time between #{article.startTime} And #{article.endTime}
and a.article_content like '%${article.articleContent}%'
and article_status=#{article.articleStatus} and art.article_type_id is not null
order by good_num+look_num desc

SELECT count(a.article_id) FROM article as a LEFT JOIN user as u on a.user_id=u.user_id LEFT JOIN (select * from article_type
parent_id=#{article.articleType.parentArticleType.articleTypeId}
) as art on a.article_type_id= art.article_type_id LEFT JOIN (select distinct(article_id),count(article_id) as comment_num from user_comment group by article_id) as c on a.article_id=c.article_id
a.article_type_id = #{article.articleType.articleTypeId}
and year(article_create_time)=year(#{article.articleCreateTime}) and month(article_create_time)= month(#{article.articleCreateTime})
and a.article_create_time between #{article.startTime} And #{article.endTime}
and a.article_content like '%${article.articleContent}%'
and article_status=#{article.articleStatus} and art.article_type_id is not null

mapper.update:
update article

    article_type_id =
        #{articleType.articleTypeId}

    article_content = #{articleContent}

where article_id = #{articleId} and user_id=#{user.userId}

update article
set article_status=#{articleStatus}
where article_id = #{articleId}
