mapper.select:
SELECT a.article_type_id, a.article_type_name, a.priority, a.article_type_create_time, a.article_type_last_edit_time, b.article_type_id as b_article_type_id, b.article_type_name as b_article_type_name, b.priority as b_priority, b.article_type_create_time as b_article_type_create_time, b.article_type_last_edit_time as b_article_type_last_edit_time FROM article_type as a left join article_type as b on a.parent_id=b.article_type_id
and a.article_type_id=#{articleTypeId} and a.article_type_name like '%${articleTypeName}%'
and a.parent_id is null
and a.parent_id=#{parentArticleType.articleTypeId}
order by a.priority desc;

mapper.update:
update article_type

    article_type_name=#{articleType.articleTypeName},
    parent_id=#{articleType.parentId},
    priority=#{articleType.priority},

        article_type_last_edit_time=#{articleType.articleTypeLastEditTime}


where article_type_id = #{articleType.articleTypeId}
